import {useState} from 'react'

import {isRequired} from '../validator'
function Login(){
    const [account, setAccount] = useState('')
    const [password, setPassword] = useState('')
    const [errorMsg, setErrorMsg] = useState(undefined)

    const handleSendFormValues = () => {
        setErrorMsg(isRequired(account))
        errorMsg && console.log({
            account,
            password
        }) 
    }


    return <div className="bg-primary">
        <h1 className="text-center brand-form">Love Travel
            <p className="brand-form__line"></p>
        </h1>

        <div id="login-form">
            <h1 className="text-center">Đăng nhập</h1>
            <div className="form-group">
                <label htmlFor="account" className="form-label">Tài khoản:</label>
                <input
                    id="account" name="login_account" type="text" className="form-control"
                    value={account}
                    onChange={(e) => setAccount(e.target.value.trim())}
                    onBlur={() => setErrorMsg(isRequired(account))}
                />
                <span className="form-msg form-msg--error">{errorMsg}</span>
            </div>
            <div className="form-group">
                <label htmlFor="password" className="form-label">Mật khẩu:</label>
                <input
                    id="password" name="login_password" type="password" className="form-control"
                    value={password}
                    onChange={(e) => setPassword(e.target.value.trim())}
                 />
                <span className="form-msg"></span>
            </div>

            <div className="row form-group">
                <span  className="col c-8"><a href="#" className="link-register"> Đăng ký ngay </a></span>

                <button className="col c-4 btn btn-login"
                    onClick={handleSendFormValues}
                >Đăng nhập
                </button>
            </div>
        </div>
    </div>
}

export default Login
