import axios from 'axios'
import {useEffect, useState} from 'react'


const apiTours = 'http://localhost:8000/api/tour/get-all-tour'
const [listTour, setListTour] = useState([])
useEffect(() => {
    const getTours = (page) => {
        axios.get(apiTours, {
            params: { page: page },
        })
        .then((res) => {
            setListTour(res.data.data)
            return res;
        })
        .catch((err) => {
            console.log(err);
        });
      };
      getTours(3);
}, [])
