import {useState, createContext} from 'react'

const OtpContext = createContext()

function OtpProvider(props, children) {
    const [otp, setOtp] = useState('')

    const value = {
        otp
    }
    
    return <OtpContext.Provider value={value} >
        {children}
    </OtpContext.Provider>
}
export {OtpContext, OtpProvider}
