import clsx from "clsx";

import styles from "../Form.module.scss";
import { isRequired } from "../validator.jsx";


function Register() {
 

  return (
    <div className="bg-primary">
      <h1 className="text-center brand-form">
        Love Travel
        <p className="brand-form__line"></p>
      </h1>
      <div id={clsx(styles.loginForm)}>
        <h1 className="text-center">Đăng nhập</h1>

        {/* Account */}
        <div className={clsx(styles.formGroup)}>
        <label htmlFor="account" className={clsx(styles.formLabel)}>
          Tài khoản:
        </label>
        <input
          id="account"
          name="login_account"
          type="text"
        />
        <span className={clsx(styles.formMsg, styles.formMsgError)}>
          {accountMsg}
        </span>
      </div>
      </div>
    </div>
  );
}

export default Register;
