import {useState} from 'react'
import clsx from 'clsx'

import bgIntro from './img_intro.jpg'
import slide1 from './slide-1.jpg'
import styles from './Intro.module.scss'

const INTRO_TITLE = "Chào mừng bạn đến với Love Travel"
const INTRO_SUB_TITLE = "Website đặt tour du lịch số 1 Việt Nam"

const slides= [
  { id: 0,
    img: slide1
  },
  { id: 1,
    img: bgIntro
  }
]
export default function Intro() {
  const [slide ,setSlides] = useState(0)

  const prevSlide = () => {
      setSlides(prev => {
        return  prev - 1;
      })
      
  }

  return (
    <>
    <div className={clsx(styles.introContainer)}>
        <div className={clsx(styles.introContent)}>
            <h1>{INTRO_TITLE}</h1>
            <h2>{INTRO_SUB_TITLE}</h2>
        </div>
        <button className={"btn"} > Đăng ký ngay  </button>
       
    </div>
     {/* Slides */}
    <div className={"grid wide container text-center"}>
          <h1>{INTRO_TITLE}</h1>
          <h2>{INTRO_SUB_TITLE}</h2>

          <div className={clsx(styles.introSlides)}>
            <img src={slides[slide].img} alt="bg" />
            <button onClick={prevSlide} className={clsx(styles.btnLeft)}><i class="fa-solid fa-chevron-left"></i> </button>
            <button onClick={() => setSlides(slide + 1)} className={clsx(styles.btnRight)}><i class="fa-solid fa-chevron-right"></i> </button>
          </div>
    </div>
    </>
    
  )
}
