import clsx from 'clsx'
import {useState,useEffect} from 'react'
import axios from 'axios'

import styles from '../User.module.scss'
import Header from '../../Header/Header'
import Toast from '../../Toast'

export default function BookedTours(){
    const [listBills, setListBills] = useState([])
    useEffect(() => {
        axios.get('http://localhost:8000/api/user/current-user/get-tour-booked',{
            headers: {authorization: localStorage.getItem('token')}
        })
        .then(res => {
            console.log(res);
            setListBills(res.data.bill)
        })
        .catch(err => {
            console.log(err);
        })
    },[])
    return (
    <>
        <Header />
        <div className="grid wide">
            <ul className="listTour">
                <ul >
                   <li className="row">
                        <div className="col l-1 m-1 c-0">STT</div>
                        <div className="col l-3 m-2 c-2">Ngày thanh toán</div>
                        <div className="col l-2 m-2 c-2">Tổng tiền</div>
                        <div className="col l-2 m-2 c-2">Số người</div>
                        <div className="col l-2 m-2 c-2">Chi tiết tour</div>
                   </li>
                {/* Content */}
                {
                    listBills.map((bill, i) => {
                        return ( 
                            <li key={i} className="row">
                            <div className="col l-1 m-1 c-0">{i+1}</div>
                            <div className="col l-2 m-2 c-2">{bill.datePay.slice(0,10)}</div>
                            <div className="col l-2 m-2 c-2">{bill.money.toLocaleString('it-IT', {style : 'currency', currency : 'VND'})}</div>
                            <div className="col l-2 m-2 c-2">{bill.totalPerson}</div>
                            <div className="col l-2 m-2 c-2">
                                <button>Xem</button>
                            </div>
                       </li>
                        )
                    })
                }
                </ul>
            </ul>
        </div>
    </>
    )
}
