import clsx from 'clsx'
import styles from './Marketing.module.scss'

import adv1 from './img-1.jpg'
import adv2 from './img-2.jpg'
import adv3 from './img-3.jpg'

const listAdv = [
    {
        id: 1,
        img: adv1,
        content: "Giảm ngay 10% các tour khi đặt lần đầu"
    },
    {
        id: 2,
        img: adv2,
        content: `Nhận hộp quà${<i class="fa-solid fa-gift"></i>}`
    },
    {
        id: 3,
        img: adv3,
        content: "Sự kiện của tháng - Event Love Travel"
    },
]

function Marketing(){

    return <div className={"container grid wide "}>
        <h1>Ưu đãi từ <span className="brand-name" style={{fontSize: 36}}>Love Travel</span></h1>
        <h2 className={clsx(styles.text)}>Nhanh tay <a href="#">Đăng ký</a> để nhận ngay ưu đãi độc quyền ngay hôm nay</h2>
        <ul className="row">
                {listAdv.map((adv, i) => {
                    return <li className={clsx("l-3", styles.advImg)} style={{backgroundImage: 'url('+adv.img+')'}}  key={i}>
                        <h2>{adv.content}</h2>
                    </li>
                })}
            </ul>
    </div>
}
 
export default Marketing
