import axios from 'axios'
import {useState, useEffect} from 'react'

const [listTour, setListTour] = useState([])
useEffect(() => {
    const getTours = (page) => {
        axios.get(apiTours, {
            params: { page: page },
        })
        .then((res) => {
            setListTour(res.data.data)
            return res;
        })
        .catch((err) => {
            console.log(err);
        });
      };
      getTours(1);
}, [])
export const infor =  listTour
