import clsx from "clsx";


import styles from './Toast.module.scss'

export default function Toast({type = 'error', title='Error', desc='Lỗi em ơi', duration = 3000}){

    const main = document.querySelector('#toast');

   

    if(main){
        const toast = document.createElement('div');
        // Auto remove toast
        const time = setTimeout(function(){
            main.removeChild(toast);
        }, duration);

        // Remove toast when click
        toast.onclick = function(e){
            if(e.target.closest('.toast__close')){
                main.removeChild(toast);
                clearTimeout(time);
            }
        }

        const timeDelay = (duration / 1000).toFixed(2);
        const icons = {
            success: "fa-solid fa-circle-check",
            info: "fa-solid fa-circle-info",
            warning: "fa-solid fa-circle-exclamation",
            error: "fa-solid fa-circle-exclamation",
        }
        const icon = icons[type];

    
        toast.classList.add('toast', `toast--${type}`);
        toast.style.animation = `slideInLeft 0.3s linear, 1s fadeOut ${timeDelay}s forwards`;
        toast.innerHTML = `
            <div class="toast__icon">
                <i class="${icon}"></i>
            </div>
            <div class="toast__body">
                <h3 class="toast__title">${title}</h3>
                <p class="toast__desc">${desc}</p>
            </div>
            <div class="toast__close">
                <i class="fa-solid fa-xmark"></i>
            </div>
        `
        main.appendChild(toast);
    }



    return (<>
        <div id="toast">

       </div>
   
       <div className="wrapToast">
           {/* <div onClick="showError();" class="btn btn--error">Show error</div> */}
       </div>
        
    </>)
}
